<template>    <div class="collect-house clearfix">        <div class="collect fll" :class="{active: currentCollect}" >            <i class="iconfont icon-shoucang"></i>            <span v-if="!currentCollect">关注此房源</span>            <span v-else>已关注</span>        </div>        <div class="share fll">            <i class="iconfont icon-fenxiang1"></i>            <span>关注此房源</span>        </div>    </div></template><script>    export default {        name: "follow",        props: {            collect: {                type: Boolean,                default: false            }        },        data() {            return {                currentCollect: this.collect            }        },        watch: {            collect(val) {                this.currentCollect = val;            }        }    }</script><style>    .icon-shoucang,.icon-fenxiang1 {        margin-right: 8px;    }    .collect,    .share {        margin-right: 30px;        cursor: pointer;    }    .share {        margin-right: 0;    }    .active {        color: #c30d23;    }</style>