<template>    <div class="clearfix">        <h2>周边配套</h2>        <div class="map-container">            <div id="traffic" style="height: 500px"></div>        </div>        <div class="wrap clearfix">            <el-tabs v-model="activeName" type="card" class="map-tab flr">                <el-tab-pane label="交通" name="1">                    <div>                        两公里以内的交通设施                    </div>                </el-tab-pane>                <el-tab-pane label="商业" name="2">                </el-tab-pane>                <el-tab-pane label="教育" name="3">                </el-tab-pane>                <el-tab-pane label="医院" name="4">                </el-tab-pane>            </el-tabs>        </div>    </div></template><script>    export default {        data() {            return {                activeName: "1"            }        },        methods: {            loadScript() {                if (document.getElementById("fooju-map")) return;//异步加载地图api                window.initMap = this.initMap;                let script = document.createElement("script");                script.src = "http://api.map.baidu.com/api?v=2.0&ak=T8aGZOH2GC1ypqIbi7ZZ35NeO0AR4tgl&callback=initMap";                script.id = "fooju-map";                document.body.appendChild(script);            },            initMap() {                this.$nextTick(() => {                    var map = new BMap.Map("traffic");                    var point = new BMap.Point(116.404, 39.915);  // 创建点坐标                    map.centerAndZoom(point, 15);                 // 初始化地图，设置中心点坐标和地图级别                })            }        },        mounted() {            this.loadScript();        }    }</script><style scoped>    .wrap {        width: 1100px;        margin: 0 auto;    }    .map-container {        width: 578px;        height: 400px;    }    .map-tab {        width: 481px;    }</style>