<template>    <div class="vue-zoom-container" v-scale="{options: currentOptions,url: minUrl}" ref="scaleBox">        <div class="vue-zoom-min-box">            <img src="/imgs/pic1.png">            <div class="vue-zoom-scale"></div>        </div>        <div class="vue-zoom-max-box">            <img src="/imgs/pic1.png">        </div>    </div></template><script>    export default {        name: "myZoom",        props: {            options: {                type: Object,                require: true            },            minUrl: {                type: String            }        },        data() {            return {                currentOptions: this.options            }        },        directives: {            scale: {                inserted(el,binding,vnode){                    console.log(binding.value)                    console.log(vnode.context.minUrl)                },                update(el,binding,vnode){                    console.log("binding" + binding.value)                    console.log(vnode.context.minUrl)                }            }        },        watch: {            options(val,old) {                this.currentOptions = val;                console.log(this)            }        },        computed: {            nowOptions() {                console.log(this)                return this.options            }        }    }</script><style scoped>    .vue-zoom-max-box {        overflow: hidden;    }</style>