<template>    <div class="switch-container clearfix">        <div class="switch-btn prev-btn fll" @click="handlePrev">        </div>        <div class="switch-box-wrap fll">            <ul class="switch-box clearfix" :style="{transform: 'translate'+translate}" >                <li v-for="(item,index) in houseDetails.jibenxinxi.pic" class="switch-item"  @click="handleSwitch(index)">                    <img :src="'/origin'+item.pic">                </li>                <li class="active" :style="{left: currentLeft+'px'}"></li>            </ul>        </div>        <div class="switch-btn next-btn flr" @click="handleNext">        </div>    </div></template><script>    import {mapState,mapMutations} from 'vuex'    export default {        props: {            options: {                type: Object            }        },        data() {            return {                translate: "(0,0)",                left: 0            }        },        methods: {            handleSwitch(index) {                if(index>this.maxIndex){                    this.translate = `(${-this.maxIndex*130}px,0)`                    this.setCurrentHouse(index);                }                else {                    this.translate = `(${-index*130}px,0)`                    this.setCurrentHouse(index);                }            },            handlePrev() {                let index = this.currentHouseIndex - 1;                if(index>this.maxIndex){                    this.setCurrentHouse(index);                }                else if(index<0){                    this.setCurrentHouse(this.houseDetails.jibenxinxi.pic.length-1);                    this.translate = `(${-this.maxIndex*130}px,0)`                }                else {                    this.setCurrentHouse(index)                    this.translate = `(${-index*130}px,0)`                }            },            handleNext() {                let index = this.currentHouseIndex + 1;                if(index>this.maxIndex){                    if(index>this.houseDetails.jibenxinxi.pic.length-1){                        this.setCurrentHouse(0);                        this.translate = `(${-0*130}px,0)`                    }                    else {                        this.setCurrentHouse(index);                    }                }                else {                    this.setCurrentHouse(index);                    this.translate = `(${-index*130}px,0)`                }            },            ...mapMutations(["setCurrentHouse"])        },        computed: {            maxIndex() {                return this.houseDetails.jibenxinxi.pic.length-4;            },            currentLeft() {                return this.currentHouseIndex*130;            },            ...mapState(["houseDetails","currentHouseIndex"])        },        directives: {        }    }</script><style lang="scss" src="~/style/switch-box.scss"></style>