<template>    <div class="switch-container clearfix">        <div class="switch-btn prev-btn fll" @click="handlePrev">        </div>        <div class="switch-box-wrap fll">            <ul class="switch-box clearfix" :style="{transform: 'translate'+translate}" >                <li v-for="(item,index) in houseDetails.jibenxinxi.pic" class="switch-item" @click="handleSwitch(index)">                    <img :src="'/origin'+item.pic">                </li>            </ul>        </div>        <div class="switch-btn next-btn flr" @click="handleNext">        </div>    </div></template><script>    import {mapState,mapMutations} from 'vuex'    export default {        props: {            options: {                type: Object            }        },        data() {            return {                translate: "(0,0)"            }        },        methods: {            handleSwitch(index) {                console.log(index);                this.setCurrentHouse(index);                this.translate = `(${-index*130}px,0)`                console.log(this.translate);            },            handlePrev() {                let index = this.currentHouseIndex - 1;                this.setCurrentHouse(index)                this.translate = `(${-index*130}px,0)`            },            handleNext() {                let index = this.currentHouseIndex + 1;                this.setCurrentHouse(index);                this.translate = `(${-index*130}px,0)`            },            ...mapMutations(["setCurrentHouse"])        },        computed: {            ...mapState(["houseDetails","currentHouseIndex"])        },        directives: {        }    }</script><style lang="scss" src="~/style/switch-box.scss"></style>