<template>    <div class="content" style="background: #f1f1f1;">        <div class="base-nav clearfix">            <breadcrumb class="fll">{{houseDetails.jibenxinxi.village}}</breadcrumb>            <collect class="flr" :collect="true"></collect>        </div>        <h1 class="house-title">{{houseDetails.jibenxinxi.title}}</h1>        <base-msg :baseObj="houseDetails"></base-msg>    </div></template><script>    import breadcrumb from '~/components/secondHand/breadcrumb'    import collect from '~/components/secondHand/collect'    import baseMsg from '~/components/secondHand/baseMsg'    import api from '~/api'    import axios from '~/plugins/axios'    export default {        layout: "secondHandDetails",        components: {            breadcrumb,            collect,            baseMsg,        },        async asyncData(context) {            console.log(context)            let houseDetails = await axios.get(api.paramToUrl(api.used_detail, {id: context.params.id}))            return {                houseDetails: houseDetails.data.data            }        },        data() {            return {}        },        mounted() {            console.log(this.houseDetails)        }    }</script><style scoped>    .content {        width: 1100px;        margin: 20px auto 0;    }    .house-title {        margin-top: 36px;        font-size: 30px;        color: #333;        cursor: pointer;    }</style>