<template>    <div class="content" style="background: #f1f1f1;">        <div class="base-nav clearfix" v-if="houseDetails">        <breadcrumb class="fll">{{houseDetails.jibenxinxi.village}}</breadcrumb>        <collect class="flr" :collect="true"></collect>        </div>        <h1 class="house-title" v-if="houseDetails">{{houseDetails.jibenxinxi.title}}</h1>        <base-msg v-if="houseDetails"></base-msg>    </div></template><script>    import breadcrumb from '~/components/secondHand/breadcrumb'    import collect from '~/components/secondHand/collect'    import baseMsg from '~/components/secondHand/baseMsg'    import api from '~/api'    import axios from '~/plugins/axios'    import {mapMutations, mapState} from 'vuex'    export default {        layout: "secondHandDetails",        components: {            breadcrumb,            collect,            baseMsg,        },        // async fetch(context){        //     let houseDetails = await axios.get(api.paramToUrl(api.used_detail, {id: context.route.params.id}))        //     context.store.commit("setHouseDetails",houseDetails.data.data)        // },        data() {            return {}        },        computed:{            ...mapState(["houseDetails"])        },        methods: {            getData() {                axios.get(api.paramToUrl(api.used_detail, {id: this.$route.params.id})).then(res => {                    this.setHouseDetails(res.data.data);                })            },            async getHouseData(){                let houseDetails = await axios.get(api.paramToUrl(api.used_detail, {id: this.$route.params.id}))                this.$store.commit("setHouseDetails",houseDetails.data.data)            },            ...mapMutations(["setHouseDetails"])        },        created() {            this.getHouseData();            // this.getData()            // console.log(this.houseDetails)        },    }</script><style scoped>    .content {        width: 1100px;        margin: 20px auto 0;    }    .house-title {        margin-top: 36px;        font-size: 30px;        color: #333;        cursor: pointer;    }</style>