<template>    <div class="content">        <div class="base-nav clearfix">            <breadcrumb class="fll">{{houseDetails.jibenxinxi.village}}</breadcrumb>            <collect class="flr" :collect="houseDetails.jibenxinxi.shoucang == 1"></collect>        </div>    </div></template><script>    import breadcrumb from '~/components/secondHand/breadcrumb'    import collect from '~/components/secondHand/collect'    import api from '~/api'    import axios from '~/plugins/axios'    import {mapStates} from 'vuex'    import common from '~/js/common'    export default {        layout: "secondHandDetails",        components: {            breadcrumb,            collect        },        data() {            return {                houseDetails: null            }        },        methods: {            async getData() {                let params = {                    id: this.$route.params.id                }                this.getLoginStatus();                if(this.userid){                    params.userid = this.userid;                }                this.houseDetails = await axios.get(api.paramToUrl(api.used_detail,params))            },            getLoginStatus: common.getLoginStatus        },        created() {            this.getData();        },        computed: {            ...mapStates([                'userid'            ])        }    }</script><style scoped>.content {    width: 1100px;    margin: 20px auto 0;}</style>