<template>    <div>        <button @click="send">send</button>        <input type="text" v-model="verify">        <button @click="submit">            提交验证码        </button>        <button @click="sendCode">修改密码</button>    </div></template><script>    import axios from 'axios'    import md5 from 'md5'    export default {        name: "test",        data() {            return {                verify: ""            }        },        methods: {            send() {                axios.get("/api/api.php?s=Msg/send_msg/mobile/18611507465").then(res => {                    console.log(res)                }).catch(err => {                    console.log(err)                })            },            sendCode() {                let params = {                    mobile: "18611507465",                    password: md5(md5("yjr1923521")) + "fujuwang"                }                axios.post(`/api/api.php?s=Login/forget`, params)            },            submit() {                axios.get(`/api/api.php?s=Msg/verify/mobile/18611507465/verify/${this.verify}`)            }        }    }</script><style scoped></style>